	public static boolean binding(Map<String, String> requestMap, JSONObject jo, WXBusinessService service) throws Exception{
		String uniqueid = requestMap.get("EventKey");//二维码带的参数
		/*List<Map<String, Object>> list = DaoSupport.executeQuerySpecifiedFields("orguser", "uniqueid='"+uniqueid+"'", "id");
		
		if(list.size()<1){
			System.out.println("orguser表中根据uniqueid查不到id");
			return false;
		}
		String sql = "insert into wxacc_orguser_relation (unionid,orgid) values(?,?)";
		DaoSupport.executeInsertOrDelete(sql, new Object[]{jo.get("unionid"), list.get(0).get("ID")});*/
		
		String orgid = service.getOrgidByUniqueid(uniqueid);
		if(orgid==null){
			System.out.println("orguser表中根据uniqueid查不到id");
			return false;
		}
		Map<String, String> map = new HashMap<String, String>();
		map.put("orgid", orgid);
		map.put("unionid", (String)jo.get("unionid"));
		service.binding(map);
		return true;
	}
	
	public static boolean unbinding(Map<String, String> requestMap, JSONObject jo, WXBusinessService service) throws Exception{
		//String sql = "delete from wxacc_orguser_relation r where r.unionid = ?";
		//DaoSupport.executeInsertOrDelete(sql, new Object[]{jo.get("unionid")});
		service.unbinding((String)jo.get("unionid"));
			return true;
	}
	


	public static void addAcc(JSONObject jo, Map<String, String> requestMap, WXBusinessService service) throws Exception {
		try{
		//String sql = "insert into wxaccount(openid,unionid,nickname,sex,province,city,country,headimgurl,appid,subscribe,language,subscribe_time,remark,groupid,tagid_list) values(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)";
		String sql = "{call proc_addacc(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)}";
		DaoSupport.executeProcedure(sql, new Object[]{jo.get("openid"),jo.get("unionid"),jo.get("nickname"),jo.get("sex"),jo.get("province"),
				jo.get("city"),jo.get("country"),jo.get("headimgurl"),requestMap.get("ToUserName"),jo.get("subscribe"),jo.get("language"),jo.get("subscribe_time"),jo.get("remark"),jo.get("groupid"),jo.get("tagid_list").toString()});
		}catch(Exception e){
			System.out.println("往wxaccount表中添加纪录异常！-->"+e);
		}
		
		int sex_temp = ((Integer)jo.get("sex")).intValue();
		char sex = (char)(sex_temp+48);
		int subscribe_temp = ((Integer)jo.get("subscribe")).intValue();
		char subscribe = (char)(subscribe_temp+48);
		WXAccount ac = new WXAccount(jo.getString("openid"), jo.getString("unionid"), jo.getString("nickname"), sex, jo.getString("province"), jo.getString("city"), jo.getString("country"), jo.getString("headimgurl"), (Date)jo.get("createdate"), requestMap.get("ToUserName"), subscribe, jo.getString("language"), new BigDecimal(((Integer)jo.get("subscribe_time")).toString()), jo.getString("remark"), jo.getString("groupid"), jo.get("tagid_list").toString());
		service.addAcc(ac);
	}
